
    var popup;

    function openPopup(videoSrc) {
      // Close the previous popup if it exists
      if (popup) {
        popup.close();
      }

      // Calculate dimensions based on viewport size
      var width = window.innerWidth || document.documentElement.clientWidth || document.body.clientWidth;
      var height = window.innerHeight || document.documentElement.clientHeight || document.body.clientHeight;

      // Create a new popup window
      popup = window.open('', 'WatchNowPopup', 'width=' + width + ',height=' + height);

      // Create a new iframe with the YouTube video URL
      var iframe = document.createElement('iframe');
      iframe.src = videoSrc; // Dynamically set the YouTube video source

      // Set iframe attributes
      iframe.width = '100%';
      iframe.height = '100%';
      iframe.frameBorder = '0';
      iframe.allowFullscreen = true;

      // Append iframe to the new popup window
      popup.document.body.appendChild(iframe);
    }

    function changeContent(movie) {
      const modal = document.getElementById('myModal');
      const modalContent = document.getElementById('modal-content');

      // Fetch the movie data from an array or external source
      const movies = getMoviesData();

      // Find the selected movie in the array
      const selectedMovie = movies.find(m => m.name.toLowerCase() === movie.toLowerCase());

      if (selectedMovie) {
        // Dynamically generate the movie modal content
        generateMovieModalContent(selectedMovie);

        // Display the modal
        modal.style.display = 'block';
      } else {
        console.error('Movie not found in the array.');
      }
    }

    function closeModal() {
      const modal = document.getElementById('myModal');
      // Close the modal
      modal.style.display = 'none';
    }

