    // Function to dynamically generate movie modal content
    function generateMovieModalContent(movieData) {
      const modalContent = document.getElementById('modal-content');
      const imageElement = document.getElementById('movie-image');
      const headingElement = document.getElementById('movie-heading');
      const descriptionElement = document.getElementById('movie-description');
      const watchNowButton = document.getElementById('watch-now-button');

      // Change content based on the selected movie data
      imageElement.src = movieData.imageSrc;
      headingElement.innerText = movieData.name;
      descriptionElement.innerText = movieData.description;

      // Update the "Watch Now" button with the actual movie name
      watchNowButton.innerText = 'Watch Now ' + movieData.name;

      // Attach the "Watch Now" functionality
      watchNowButton.onclick = function() {
        openPopup(movieData.videoSrc);
      };
    }